import React, { Component } from "react";
import ImageCard from "./components/ImageCard";
import Wrapper from "./components/Wrapper";
import Title from "./components/Title";
import Score from "./components/Score";
import images from "./images.json";
import "./App.css";

let correctGuesses = 0;
let bestScore = 0;
let clickMessage = "Select your team of Avengers to defeat the Mad Titan Thanos! Click on the same one twice and you snaps!";

class App extends Component {

  // Setting this.state.matches to the matches json array
  state = {
      matches,
      correctGuesses,
      bestScore,
      clickMessage
  };

  setClicked = id => {

      // Make a copy of the state matches array to work with
      const matches = this.state.matches;

      // Filter for the clicked match
      const clickedMatch = matches.filter(match => match.id === id);

      // If the matched image's clicked value is already true, 
      // do the game over actions
      if (clickedMatch[0].clicked){

          console.log ("Correct Guesses: " + correctGuesses);
          console.log ("Best Score: " + bestScore);

          correctGuesses = 0;
          clickMessage = "Bummer! You already clicked on this one."

          for (let i = 0 ; i < matches.length ; i++){
              matches[i].clicked = false;
          }

          this.setState({clickMessage});
          this.setState({ correctGuesses });
          this.setState({matches});

      // Otherwise, if clicked = false, and the user hasn't finished
      } else if (correctGuesses < 11) {

          // Set its value to true
          clickedMatch[0].clicked = true;

          // increment the appropriate counter
          correctGuesses++;

          clickMessage = "Great! You haven't click on that one yet! Keep going!";

          if (correctGuesses > bestScore){
              bestScore = correctGuesses;
              this.setState({ bestScore });
          }

          // Shuffle the array to be rendered in a random order
          matches.sort(function(a, b){return 0.5 - Math.random()});

          // Set this.state.matches equal to the new matches array
          this.setState({ matches });
          this.setState({correctGuesses});
          this.setState({clickMessage});
      } else {

          // Set its value to true
          clickedMatch[0].clicked = true;

          // restart the guess counter
          correctGuesses = 0;

          // Egg on the user to play again
          clickMessage = "WOW!!! You got ALL of them!!! Now, let's see if you can do it again!";
          bestScore = 12;
          this.setState({ bestScore });

          for (let i = 0 ; i < matches.length ; i++){
              matches[i].clicked = false;
          }

          // Shuffle the array to be rendered in a random order
          matches.sort(function(a, b){return 0.5 - Math.random()});

          // Set this.state.matches equal to the new matches array
          this.setState({ matches });
          this.setState({correctGuesses});
          this.setState({clickMessage});

      }
  };


  // Map over this.state.friends and render a FriendCard component for each friend object
  render() {
    return (
      <Wrapper>
        <h3 className="message">
          {this.state.clickMessage}
        </h3>
        <Score></Score>
        <br />
        <Title>Avenge The Fallen</Title>
        {this.state.images.map(image => (
          <ImageCard
            id={image.id}
            key={image.id}
            name={image.name}
            image={image.image}
          />
        ))}
      </Wrapper>
    );
  }

}

export default App;
